package mi.miui.quicksetting;import android.content.Intent;import android.os.Build;import android.service.quicksettings.TileService;import android.support.annotation.RequiresApi;import android.content.pm.PackageManager;import android.widget.Toast;@RequiresApi(api = Build.VERSION_CODES.N)public class basese extends TileService {String p;public void inp(String path){this.p=path;}@Override public void onClick(){ super.onClick();PackageManager packageManager = getPackageManager();Intent intent=new Intent();   intent =packageManager.getLaunchIntentForPackage(p);    if(intent==null){  Toast.makeText(this,"app not find!",10).show();}  startActivity(intent); }}
